import React, { useState } from "react";
import './clock.css';
const Clock = () => {
    const curTime = new Date().toLocaleTimeString();
    const [time, setTime] = useState(curTime);
    const [alarm, setAlarm] = useState(0);

    const update = () => {
        const curTime = new Date().toLocaleTimeString();
        setTime(curTime);
    }
    setInterval(() => {
        update()
    }, 1000)

    function addMinute(){
        setAlarm(alarm + 1);
    }
    let minutes = addMinute();
    if(minutes == addMinute()){
        alert("The Alarm")
    }
    return (
        <div className="clock">
            <div className="digital_clock">
                <h1>Digital Clock</h1>
                <div className="timer-clock">
                    <h2>{time}</h2>
                </div>
                <h1>Set Alarm</h1>
                <p>{alarm}</p>
                <button onClick={addMinute}>Set Minutes</button>
                
            </div>
        </div>
    );
}

export default Clock
