import React, { useState } from "react";
import './clock.css'
const Clock = () => {
    const curTime = new Date().toLocaleTimeString();
    const [time, setTime] = useState(curTime);

    const update = () => {
        const curTime = new Date().toLocaleTimeString();
        setTime(curTime);
    }
    setInterval(() => {
        update()
    }, 1000)
    return (
        <div className="clock">
            <div className="digital_clock">
                <h1>Digital Clock</h1>
                <h2>{time}</h2>
            </div>
        </div>
    );
}

export default Clock
